mapper.select:
select r.RECORD_ID as STOCKOUT_ID, r.RECORD_CUSTOMERID as STOCKOUT_CUSTOMER_ID, c.CUSTOMER_NAME as STOCKOUT_CUSTOMER_NAME, r.RECORD_GOODID as STOCKIN_GOOD_ID, g.GOOD_NAME as STOCKOUT_GOOD_NAME, r.RECORD_NUMBER as STOCKOUT_NUMBER, r.RECORD_TIME as STOCKOUT_TIME, r.RECORD_PERSON as STOCKOUT_PERSON, r.RECORD_REPOSITORYID as STOCKOUT_REPOSITORY_ID, res.REPO_NAME as STOCKOUT_REPOSITORY, r.RECORD_SHELVESID as STOCKOUT_SHELVES_ID, sh.SHELVES_NAME as STOCKOUT_SHELVES from wms_record_out r LEFT JOIN wms_goods g ON r.RECORD_GOODID = g.GOOD_ID LEFT JOIN wms_customer c ON r.RECORD_CUSTOMERID = c.CUSTOMER_ID LEFT JOIN wms_respository res ON r.RECORD_REPOSITORYID = res.REPO_ID LEFT JOIN wms_shelves sh ON r.RECORD_SHELVESID = sh.SHELVES_ID
and r.RECORD_REPOSITORYID = #{repositoryID}
and r.RECORD_TIME >= #{startDate,jdbcType=DATE}
and r.RECORD_TIME <= #{endDate,jdbcType=DATE}
