mapper.select:
select s.RECORD_REPOSITORY as repositoryID, r.REPO_NAME as repository, s.RECORD_SHELVES as shelvesID, ss.SHELVES_NAME as shelves, s.RECORD_NUMBER as number, g.GOOD_ID as goodsID, g.GOOD_NAME as goodsName, g.GOOD_SIZE as goodsSize, g.GOOD_VALUE as goodsValue, c.CATEGORY_NAME as goodsType from wms_record_storage s LEFT JOIN wms_respository r ON s.RECORD_REPOSITORY = r.REPO_ID LEFT JOIN wms_goods g ON s.RECORD_GOODID = g.GOOD_ID LEFT JOIN wms_shelves ss ON s.RECORD_SHELVES = ss.SHELVES_ID LEFT JOIN wms_category c ON g.CATEGORY_ID = c.CATEGORY_ID
and s.RECORD_REPOSITORY = #{repositoryID}
and s.RECORD_SHELVES = #{shelvesID}

select s.RECORD_REPOSITORY as repositoryID, r.REPO_NAME as repository, s.RECORD_SHELVES as shelvesID, ss.SHELVES_NAME as shelves, s.RECORD_NUMBER as number, g.GOOD_ID as goodsID, g.GOOD_NAME as goodsName, g.GOOD_SIZE as goodsSize, g.GOOD_VALUE as goodsValue, c.CATEGORY_NAME as goodsType from wms_record_storage s LEFT JOIN wms_respository r ON s.RECORD_REPOSITORY = r.REPO_ID LEFT JOIN wms_goods g ON s.RECORD_GOODID = g.GOOD_ID LEFT JOIN wms_shelves ss ON s.RECORD_SHELVES = ss.SHELVES_ID LEFT JOIN wms_category c ON g.CATEGORY_ID = c.CATEGORY_ID where s.RECORD_GOODID = #{goodsID}
and s.RECORD_REPOSITORY = #{repositoryID}
and s.RECORD_SHELVES = #{shelvesID}

select s.RECORD_REPOSITORY as repositoryID, r.REPO_NAME as repository, s.RECORD_SHELVES as shelvesID, ss.SHELVES_NAME as shelves, s.RECORD_NUMBER as number, g.GOOD_ID as goodsID, g.GOOD_NAME as goodsName, g.GOOD_SIZE as goodsSize, g.GOOD_VALUE as goodsValue, c.CATEGORY_NAME as goodsType from wms_record_storage s LEFT JOIN wms_respository r ON s.RECORD_REPOSITORY = r.REPO_ID LEFT JOIN wms_goods g ON s.RECORD_GOODID = g.GOOD_ID LEFT JOIN wms_shelves ss ON s.RECORD_SHELVES = ss.SHELVES_ID LEFT JOIN wms_category c ON g.CATEGORY_ID = c.CATEGORY_ID where g.GOOD_NAME like concat("%",concat(#{goodsName},"%"))
and s.RECORD_REPOSITORY = #{repositoryID}
and s.RECORD_SHELVES = #{shelvesID}

mapper.update:
update 
	wms_record_storage
set
	RECORD_NUMBER = #{number}
where
	RECORD_GOODID = #{goodsID}
	and RECORD_REPOSITORY = #{repositoryID}
	and RECORD_SHELVES = #{shelvesID}
